import Layout from '../../components/layout';

// Will return weather from the search query
const Weather = (props) => {
  return (
    <Layout>
      <div>
        <h1>You've hit Weather</h1>
      </div>
    </Layout>
  )
}

export async function getServerSideProps(ctx) {
  const res = await fetch(`https://api.opencagedata.com/geocode/v1/json?key=9509e53adb5442c8829342e5c5b15438&q=${ctx.query.city}`);
  const data = await res.json();
  // access lat and long from data
  const { lat, lng } = data.results[0].geometry;
  console.log(lat, lng)
  // search for weather data with lat and long data
  const res_2 = await fetch(`https://api.openweathermap.org/data/2.5/onecall?lat=${lat}&lon=${lng}&appid=${process.env.WEATHER_API}&units=imperial`);
  const data_2 = await res_2.json()

  console.log(data_2);

  // return to props
  return {
    props: {
      results: data.results
    }
  }
}

export default Weather;
