import { useRouter } from 'next/router';

const HourlyLink = ({ children, href, as, ...rest }) => {
  const hourly = {...rest};
  const router = useRouter();
  const handler = ev => {
    ev.preventDefault();
    router.push(href, as)
  }

  return (
    <a href={href} onClick={handler} hourly={hourly}>
      {children}
    </a>
  )
}

export default HourlyLink;
