= Break up functions

*/
import Layout from '../components/layout/layout';
import utilStyles from '../styles/utils.module.scss';

const Homepage = () => {
  return (
    <Layout>
      <h1 className={`${utilStyles.title} ${utilStyles.mtb3}`}>Get some weather</h1>
      <p className={`${utilStyles.subtitle}`}>
        Search for your location in the search bar<br />
        Or click common cities above.
      </p>
    </Layout>
  )
}

export async function getServerSideProps(ctx) {
  const cities = [
    {
      lat: 40.014984,
      lng: -105.270546
    },
    {
      lat: 42.317432,
      lng: -83.026772
    },
    {
      lat: 37.865101,
      lng: -119.538330
    },
    {
      lat: 55.766174,
      lng: 37.610561
    },
    {
      lat: 19.432526,
      lng: -99.194954
    },
    {
      lat: 48.855701,
      lng: 2.317000
    },
    {
      lat: 48.108334,
      lng: 16.316944
    },
    {
      lat: 41.913399,
      lng: -87.648201
    },
    {
      lat: -23.566221,
      lng: -46.667358
    },
    {
      lat: -33.927551,
      lng: 18.457073
    }
  ];
  const city = cities[Math.floor(Math.random() * cities.length)];
  // search for weather data with lat and long data
  const weather_res = await fetch(`https://api.openweathermap.org/data/2.5/onecall?lat=${city.lat}&lon=${city.lng}&appid=${process.env.WEATHER_API}&units=metric`);
  const weather_data = await weather_res.json();

  console.log(weather_data);
  // return to props
  return {
    props: {
      weather: weather_data
    }
  }
}

export default Homepage;
