import { useRouter } from 'next/router';

const WeatherLink = ({ href, as, children, city }) => {
  const router = useRouter();
  const handler = (ev) => {
    ev.preventDefault();
    router.push(href, as, { query: {city} })
  }
  return (
    <a href={href} as={as} onClick={handler}>{children}</a>
  )
}

export default WeatherLink;
