import { useRouter } from 'next/router';

const HourlyLink = ({ href, as, hourly }) => {
  console.log(props);
  const router = useRouter();
  const handler = ev => {
    ev.preventDefault();
    router.push(href, as)
  }

  // return (
  //   <a href={href} onClick={handler} hourly={hourly}>
  //     {children}
  //   </a>
  // )
  return <a onClick={handler} hourly={hourly}>{props.children}</a>
}

export default HourlyLink;
