import { useRouter } from 'next/router';

const HourlyLink = ({ href, as, hourly }) => {
  const router = useRouter();
  const handler = ev => {
    ev.preventDefault();
    router.push(href, as)
  }

  return <a onClick={handler} hourly={hourly}>{children}</a>
}

export default HourlyLink;
