import { getIcon } from '../../utils/weatherHelper';
import { capitalize } from '../../utils/stringHelper';

const Hourly = ({ hourly }) => {
  console.log(hourly)
  return (
    <div>
      {
        hourly.map((hour, idx) => {
          return (
            <div key={idx}>
              <div>
                <img src={getIcon(current.weather[0].icon)} alt={current.weather[0].description} />
              </div>
              <div>
                <p>Temp: {Math.floor(hour.temp)}</p>
                <small>Feels like: {Math.floor(hour.feels_like)}</small>
              </div>
              <div>
                <p>Weather: {`${capitalize(current.weather[0].description)}`}</p>
              </div>
            </div>
          )
        })
      }
    </div>
  )
}

export default Hourly;
