import Link from 'next/link';

import Layout from '../../components/layout/layout';

const Hourly = ({location}) => {
  const city = 'Philadelphia';
  console.log(location);
  return (
    <Layout>
      <h1>Hi from Hourly</h1>
      <Link href={`/city/weather?city=${city}`}><a>Go back</a></Link>
    </Layout>
  )
}

export async function getServerSideProps(ctx) {
  const res = await fetch(`https://api.opencagedata.com/geocode/v1/json?key=9509e53adb5442c8829342e5c5b15438&q=${ctx.query.city}`);
  const data = await res.json();

  return {
    props: {
      location: data
    }
  }
}

export default Hourly;
